import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
      super(props);
      this.state = { 
        language: [
          {vote: 0, name: 'php', text:"Click Here"},
          {vote: 0, name: 'pyhton', text:"Click Here"},
          {vote: 0, name: 'go', text:"Click Here"},
          {vote: 0, name: 'java', text:"Click Here"},
        ]
       };

  }

  incrementCount = language => {
    if (language === "php"){
      this.setState({ language: this.state.language.map(item => {
        if (item.name === "php") {
          item.vote = item.vote + 1;
        } else {
          return item;
        }
      }) });
    } else if(language ==="pyhton"){
      this.setState({vote:this.state.language[1].vote+1});
    }else if(language ==="go"){
      this.setState({vote:this.state.language[2].vote+1});
    }else if(language ==="java"){
      this.setState({vote:this.state.language[3].vote+1});
    }

};

  render() {

      return (

        <div className="languages">
          <h1>Vote Your Language!</h1>


          <div className="language">
            <div className="languageCount">{ this.state.language[0].vote }</div>
            <div className="languageName">PHP</div>
            <div className="click" onClick={() =>  this.incrementCount("php")}>
              Clicks Here
            </div>
          </div>
          <div className="language">
            <div className="languageCount">{ this.state.language[1].vote }</div>
            <div className="languageName">PYHTON</div>
            <div className="click" onClick={()=> this.incrementCount("pyhton")}>
            Clicks Here
            </div>
          </div>
          <div className="language">
            <div className="languageCount">{ this.state.language[2].vote }</div>
            <div className="languageName">GO</div>
            <div className="click" onClick={()=> this.incrementCount("go") }>
            Clicks Here
            </div>
          </div>
          <div className="language">
            <div className="languageCount">{ this.state.language[3].vote }</div>
            <div className="languageName">JAVA</div>
            <div className="click" onClick={()=> this.incrementCount("java") }>
            Clicks Here
            </div>
          </div>
        </div>
      );
  }
}

export default App;
