import React, { Component } from 'react';
import './App.css';

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
          language: [
            {vote: 0, name: 'php'},
            {vote: 0, name: 'pyhton'},
            {vote: 0, name: 'go'},
            {vote: 0, name: 'java'},
          ],

        };
    }

  incrementCount = language => {
    const newLanguage = [...this.state.language];
    newLanguage.map((lang,i) => {
      if(newLanguage[i].name === language) {
        newLanguage[i].vote++;
      }
      return lang;
    });
    this.setState({
      language: newLanguage
    })
  };


  decreaseCount = language => {
    const newLanguage = [...this.state.language];
    newLanguage.map((lang,i) => {
      if(newLanguage[i].name === language) {
        newLanguage[i].vote--;
      }
      return lang;
    });
    this.setState({
      language: newLanguage
    })
  };

  render(){
    return(
      <div>
        <h1>Vote Your Language!</h1>
        <div className="languages">
          {
            this.state.language.map((lang, i) =>       
            <div className="language">
               <div className="click" onClick={() =>  this.decreaseCount(this.state.language[i].name)}>
              Clicks Here
              </div>
              <div className="languageCount">{ this.state.language[i].vote }</div>
              <div className="languageName">{this.state.language[i].name}</div>
              <div className="click" onClick={() =>  this.incrementCount(this.state.language[i].name)}>
              Clicks Here
              </div>

            </div>
            )
          }
        </div>
      </div>
    );
  }
}

export default App;
