import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
      super(props);
      this.state = { 
        count: 0, 
        count1: 0, 
        count2: 0, 
        count3: 0
       };

  }

  incrementCount = (language) => {

    if(language === "php"){
      this.setState({ count:this.state.count + 1 });
    } else if(language==="pyhton"){
      this.setState({count1:this.state.count1+1});
    }else if(language==="go"){
      this.setState({count2:this.state.count2+1});
    }else if(language==="java"){
      this.setState({count3:this.state.count3+1});
    }

};

  render() {

      return (

        <div className="languages">
          <h1>Vote Your Language!</h1>
          <div className="language">
            <div className="languageCount">{ this.state.count }</div>
            <div className="languageName">PHP</div>
            <div className="click" onClick={() =>  this.incrementCount("php")}>
              Clicks Here
            </div>
          </div>
          <div className="language">
            <div className="languageCount">{ this.state.count1 }</div>
            <div className="languageName">PYHTON</div>
            <div className="click" onClick={()=> this.incrementCount("pyhton")}>
            Clicks Here
            </div>
          </div>
          <div className="language">
            <div className="languageCount">{ this.state.count2 }</div>
            <div className="languageName">GO</div>
            <div className="click" onClick={()=> this.incrementCount("go") }>
            Clicks Here
            </div>
          </div>
          <div className="language">
            <div className="languageCount">{ this.state.count3 }</div>
            <div className="languageName">JAVA</div>
            <div className="click" onClick={()=> this.incrementCount("java") }>
            Clicks Here
            </div>
          </div>
        </div>
      );
  }
}

export default App;
