import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
      super(props);
      this.state = { 
        language: [
          {vote: 0, name: 'php', text:"Click Here"},
          {vote: 0, name: 'pyhton', text:"Click Here"},
          {vote: 0, name: 'go', text:"Click Here"},
          {vote: 0, name: 'java', text:"Click Here"},
        ]
       };

  }

  incrementCount = language => {
    if (language === "php"){
      this.setState({ language: this.state.language[0].vote+1 });
    } else if(language ==="pyhton"){
      this.setState({vote:this.state.language[1].vote+1});
    }else if(language ==="go"){
      this.setState({vote:this.state.language[2].vote+1});
    }else if(language ==="java"){
      this.setState({vote:this.state.language[3].vote+1});
    }

};

render(){
  return(
    <>
      <h1>Vote Your Language!</h1>
      <div className="languages">
        {
          this.state.languages.map((lang, i) => 
            <div key={i} className="language">
              <div className="voteCount">
                {lang.votes}
              </div>
              <div className="languageName">
                {lang.name}
              </div>
              <button onClick={this.vote.bind(this, i)}>Click Here</button>
            </div>
          )
        }
      </div>
    </>
  );
}
}

export default App;
