import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
      super(props);
      this.state = { 
        language: [
          {vote: 0, name: 'php', text:"Click Here"},
          {vote: 0, name: 'pyhton', text:"Click Here"},
          {vote: 0, name: 'go', text:"Click Here"},
          {vote: 0, name: 'java', text:"Click Here"},
        ]
       };

  }

  incrementCount = (language) => {

    if(language[0].name === "php"){
      this.setState({ vote:this.state.language[0] + 1 });
    } else if(language[1].name==="pyhton"){
      this.setState({vote:this.state.language[1]+1});
    }else if(language[2].name==="go"){
      this.setState({vote:this.state.language[2]+1});
    }else if(language[3].name==="java"){
      this.setState({vote:this.state.language[3]+1});
    }

};

  render() {

      return (

        <div className="languages">
          <h1>Vote Your Language!</h1>

          this.state.language.map((lang, i) => 
							<div key={i} className="language">
                  <div className="languageCount">{ this.state.language[0]}</div>
                  <div className="languageName">PHP</div>
                  <div className="click" onClick={() =>  this.incrementCount("php")}>
                    Clicks Here
                  </div>
							</div>

        </div>
      );
  }
}

export default App;
