import React, { Component } from 'react';
import './App.css';

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
          language: [
            {vote: 0, name: 'php'},
            {vote: 0, name: 'pyhton'},
            {vote: 0, name: 'go'},
            {vote: 0, name: 'java'},
          ],      
        };
    }

  vote = (language,action) => {
    const newLanguage = [...this.state.language];
    newLanguage.map((lang,i) => {

        if( action === "inc"  && newLanguage[i].name === language){
          newLanguage[i].vote++;
        }
        else if( action === "dec" && newLanguage[i].name === language){   
            if(newLanguage[i].vote>0){
              newLanguage[i].vote--;
            }
        }
        return lang;

    });
    this.setState({
      language: newLanguage
    })
  };

  render(){
    return(
      <div>
        <h1>Vote Your Language!</h1>
        <div className="languages">
          {
            this.state.language.map((lang, i) =>
            <div className="language" key={lang.name}>
               <button onClick={() =>  this.vote(this.state.language[i].name,"dec")}>
              Clicks Here
              </button>
              <div className="languageCount">{ this.state.language[i].vote }</div>
              <div className="languageName">{this.state.language[i].name}</div>
              <button onClick={() =>  this.vote(this.state.language[i].name,"inc")}>
              Clicks Here
              </button>
            </div>
            )
          }
        </div>
      </div>
    );
  }
}

export default App;
